    private void btnGuardarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnGuardarActionPerformed
        String nombreMateria = txtNombreMateria.getText();
        int dificultad = Integer.parseInt(txtDificultad.getText());

        materiaDAO.insertarMateria(nombreMateria, dificultad);
        cargarMateriasEnTabla();  // Refrescar la tabla después de guardar
        llenarComboBoxMaterias(); // Actualizar los ComboBox después de guardar
    }//GEN-LAST:event_btnGuardarActionPerformed

    private void btnCompararActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCompararActionPerformed
        String materia1 = (String) comboBoxMateria1.getSelectedItem();
        String materia2 = (String) comboBoxMateria2.getSelectedItem();

        int dificultad1 = materiaDAO.obtenerDificultad(materia1);
        int dificultad2 = materiaDAO.obtenerDificultad(materia2);

        int diferencia = Math.abs(dificultad1 - dificultad2);
        labelResultado.setText("Nivel de dificultad: " + diferencia);
    }//GEN-LAST:event_btnCompararActionPerformed

