    private void btnEliminarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnEliminarActionPerformed
        int filaSeleccionada = tablaMaterias.getSelectedRow();

        if (filaSeleccionada == -1) {
            JOptionPane.showMessageDialog(this, "Por favor, selecciona una materia para eliminar.");
        } else {
            int id = (int) tablaMaterias.getValueAt(filaSeleccionada, 0);  // Obtener el ID de la fila seleccionada
            materiaDAO.eliminarMateria(id);  // Llamar al método para eliminar la materia de la BD
            cargarMateriasEnTabla();  // Refrescar la tabla después de eliminar
        }
    }//GEN-LAST:event_btnEliminarActionPerformed

    private void cargarMateriasEnTabla() {
        DefaultTableModel modelo = materiaDAO.cargarMaterias();
        tablaMaterias.setModel(modelo);
    }
    
    private void llenarComboBoxMaterias() {
        comboBoxMateria1.removeAllItems();
        comboBoxMateria2.removeAllItems();

        DefaultTableModel modelo = materiaDAO.cargarMaterias();
        for (int i = 0; i < modelo.getRowCount(); i++) {
            String nombreMateria = modelo.getValueAt(i, 1).toString();
            comboBoxMateria1.addItem(nombreMateria);
            comboBoxMateria2.addItem(nombreMateria);
        }
    }
    
    
